Add-Type -Name WinDPI -Namespace Native -MemberDefinition '
[DllImport("SHCore.dll", SetLastError = true)]
public static extern bool SetProcessDpiAwareness(PROCESS_DPI_AWARENESS awareness);

public enum PROCESS_DPI_AWARENESS
{
    Process_DPI_Unaware = 0,
    Process_System_DPI_Aware = 1,
    Process_Per_Monitor_DPI_Aware = 2
}
'

[Native.WinDPI]::SetProcessDPIAwareness([Native.WinDPI+PROCESS_DPI_AWARENESS]::Process_Per_Monitor_DPI_Aware) | Out-Null

Start-Job {

    do {

        if (!(Get-WmiObject Win32_Process -Filter "Name='powershell.exe' AND CommandLine LIKE '%SteamAchievementNotifier.ps1%'")) {
        & Start-Process -FilePath "powershell.exe" -ArgumentList "-WindowStyle Hidden -ExecutionPolicy Bypass -File $env:localappdata\SteamAchievementNotifier\SteamAchievementNotifier.ps1"
        [System.GC]::Collect()
        
        }
    
    } while($true)

} | Out-Null

