function Set-APIText {

    if ([String]::IsNullOrWhiteSpace(($apikeycheck))) {
        
        $steamapi.Text = "No Steam API Key Detected"
        $steamapi.ForeColor = "Red"

    } else {

        $steamapi.Text = $apikeycheck
        $steamapi.ForeColor = "White"

    }

}

function Set-64Text {

    if ([String]::IsNullOrWhiteSpace(($steam64check))) {

        $steam64.Text = "No Steam64 ID Detected"
        $steam64.ForeColor = "Red"

    } else {
        
        $steam64.Text = $steam64check
        $steam64.ForeColor = "White"

    }

}

Set-APIText
Set-64Text

function Set-API {
    
    $apiresult = $steamapi.Text.ToString()

    if ([String]::IsNullOrWhiteSpace(($apiresult))) {

        [System.Windows.Forms.MessageBox]::Show("Please enter your Steam API Key.","Steam Achievement Notifier","OK","Error")
        
        Set-APIText

    } elseif ($apiresult -eq "No Steam API Key Detected") {

        [System.Windows.Forms.MessageBox]::Show("Please enter your Steam API Key.","Steam Achievement Notifier","OK","Error")
        
        Set-APIText
    
    } else {

        $apiconf = [System.Windows.Forms.MessageBox]::Show("API Key will be updated to:`n`n$apiresult","Steam Achievement Notifier","OKCancel","Information")
    
        if ($apiconf -eq "OK") {
            
            $apiresult | Out-File "$env:localappdata\SteamAchievementNotifier\Store\apikey.txt"
            $apikeychecknew = (Get-Content "$env:localappdata\SteamAchievementNotifier\Store\apikey.txt")
            $steam64checknew = (Get-Content "$env:localappdata\SteamAchievementNotifier\Store\steam64id.txt")

            $steamapi.Text = $apikeychecknew
            $steamapi.ForeColor = "White"

            $usercheckapi = Invoke-RestMethod "http://api.steampowered.com/ISteamUser/GetPlayerSummaries/v0002/?key=$apikeychecknew&steamids=$steam64checknew"
            $userapi = $usercheckapi.response.players.personaname

            if (!($userapi)) {

                $welcome.Text = "No Steam User Detected"
                $welcome.ForeColor = "Red"

                $steamuser.Text = "No Steam User Detected"
                $steamuser.ForeColor = "Red"

            } else {
                    
                $welcome.Text = $userapi
                $welcome.ForeColor = "White"

                $steamuser.Text = $userapi
                $steamuser.ForeColor = "White"

                $user = $userapi
                $apikeycheck = $apikeychecknew
                $steam64check = $steam64checknew

                Set-APIText
                Set-64Text

            }

        }

    }

}

$apisavebtn.Add_Click({ Set-API })

function Set-64 {

    $64result = $steam64.Text.ToString()
    
    if ([String]::IsNullOrWhiteSpace(($64result))) {

        [System.Windows.Forms.MessageBox]::Show("Please enter your Steam64 ID.","Steam Achievement Notifier","OK","Error")
        
        Set-64Text

    } elseif ($64result -eq "No Steam64 ID Detected") {
    
        [System.Windows.Forms.MessageBox]::Show("Please enter your Steam64 ID.","Steam Achievement Notifier","OK","Error")
        
        Set-64Text

    } else {

        $64conf = [System.Windows.Forms.MessageBox]::Show("Steam64 ID will be updated to:`n`n$64result","Steam Achievement Notifier","OKCancel","Information")

        if ($64conf -eq "OK") {
            
            $64result | Out-File "$env:localappdata\SteamAchievementNotifier\Store\steam64id.txt"
            $apikeychecknew = (Get-Content "$env:localappdata\SteamAchievementNotifier\Store\apikey.txt")
            $steam64checknew = (Get-Content "$env:localappdata\SteamAchievementNotifier\Store\steam64id.txt")

            $steam64.Text = $steam64checknew
            $steam64.ForeColor = "White"

            $usercheck64 = Invoke-RestMethod "http://api.steampowered.com/ISteamUser/GetPlayerSummaries/v0002/?key=$apikeychecknew&steamids=$steam64checknew"
            $user64 = $usercheck64.response.players.personaname

            if (!($user64)) {

                $welcome.Text = "No Steam User Detected"
                $welcome.ForeColor = "Red"

                $steamuser.Text = "No Steam User Detected"
                $steamuser.ForeColor = "Red"

            } else {
                    
                $welcome.Text = $user64
                $welcome.ForeColor = "White"

                $steamuser.Text = $user64
                $steamuser.ForeColor = "White"

                $user = $user64
                $apikeycheck = $apikeychecknew
                $steam64check = $steam64checknew

                Set-APIText
                Set-64Text

            }

        }

    }

}

$64savebtn.Add_Click({ Set-64 })

function Get-CustomSound {

    $openwindow = $open.ShowDialog()

    if ($openwindow -eq "OK") {

        $open.FileName | Out-File "$env:localappdata\SteamAchievementNotifier\Store\sound.txt"
        $soundcheck = (Get-Content "$env:localappdata\SteamAchievementNotifier\Store\sound.txt")
        $soundlbl.Text = $soundcheck

    }

}

$soundbrowse.Add_Click({ Get-CustomSound })

function SteamCreds-Refresh {
    
    $apikeycheck = (Get-Content "$env:localappdata\SteamAchievementNotifier\Store\apikey.txt")
    $steam64check = (Get-Content "$env:localappdata\SteamAchievementNotifier\Store\steam64id.txt")

    if ([String]::IsNullOrWhiteSpace(($apikeycheck))) {

        $steamapi.Text = "No Steam API Key Detected"
        $steamapi.ForeColor = "Red"

    } else {

        $steamapi.Text = $apikeycheck
        $steamapi.ForeColor = "White"

    }

    if ([String]::IsNullOrWhiteSpace(($steam64check))) {

        $steam64.Text = "No Steam64 ID Detected"
        $steam64.ForeColor = "Red"

    } else {

        $steam64.Text = $steam64check
        $steam64.ForeColor = "White"

    }
    
    $steamcreds.ShowDialog()

}

$button1.Add_Click({ SteamCreds-Refresh })

function TestNotification {

    Start-Job {

        $wav = New-Object System.Media.SoundPlayer
        $wav.SoundLocation = (Get-Content "$env:localappdata\SteamAchievementNotifier\Store\sound.txt")

        New-BurntToastNotification -Text "Steam Achievement Notifier","Your notifications are working correctly." -UniqueIdentifier "Test"  -AppLogo "$env:localappdata\SteamAchievementNotifier\steamcheev.ico" -Silent
        Sleep -Milliseconds 275
        $wav.Play()
        Sleep 3
        Remove-BTNotification -UniqueIdentifier "Test"

    } | Out-Null

}

function Deselect {
    
    $steamcreds.ActiveControl = $clicklbl
    $steamcreds.Close()
    $steamcreds.Refresh()
    $form.Refresh()

}

